    private static void getEmails(Map<String, String> urls) {
        Pattern p
                = Pattern.compile(
                "[a-zA-Z0-9]"
                        + "[a-zA-Z0-9_.]"
                        + "*@[a-zA-Z0-9]"
                        + "+([.][a-zA-Z]+)+");
        String linkString = "\\b(https?|ftp|file)://[-a-zA-Z0-9+&@#/%?=~_|!:,.;]*[-a-zA-Z0-9+&@#/%=~_|]";
        Pattern linkPattern = Pattern.compile(linkString, Pattern.CASE_INSENSITIVE);
        Pattern href = Pattern.compile("\"([^\"]*)\"");
        Map<String, List<String>> exbEmails = new HashMap<>();
        urls.forEach((key, value) -> {
            try {
                List<String> emails = new ArrayList<>();
                Deque<String> unvisitedLinks = new ArrayDeque<>();
                unvisitedLinks.add(value);
                final boolean[] takeLinks = new boolean[1];
                takeLinks[0] = true;
                while (!unvisitedLinks.isEmpty() && emails.isEmpty()) {
                    URL myURL = new URL(unvisitedLinks.peek());
                    new BufferedReader(
                            new InputStreamReader(myURL.openStream(), StandardCharsets.UTF_8))
                            .lines().forEach(line -> {
                                Matcher matcher = p.matcher(line);
                                Matcher linkMatch = linkPattern.matcher(line);
                                if (matcher.find()) {
                                    emails.add(matcher.group());
                                    System.out.println(key + " " + matcher.group());
                                }
                                if (takeLinks[0] && linkMatch.find() && !unvisitedLinks.contains(line) && (line.toLowerCase().contains("contact") || line.toLowerCase().contains("facebook"))) {
                                    Matcher hrefMatch = href.matcher(line);
                                    if (hrefMatch.find()) {
                                        unvisitedLinks.add(hrefMatch.group(1));
                                    } else {
                                        unvisitedLinks.add(line);
                                    }
                                }
                            });
                    takeLinks[0] = false;
                    unvisitedLinks.removeFirst();
                }
                System.out.println(key + "  --------");
                System.out.println(emails);
                exbEmails.put(key, emails);
            } catch (Exception e) {

            }

        });
        System.out.println(exbEmails);
    }

    public static Document parse(String htmlData) {
        Document document;
        DocumentBuilderFactory factory = DocumentBuilderFactory
                .newInstance();
        try {
            DocumentBuilder builder = factory.newDocumentBuilder();
            document = builder.parse(htmlData);

        } catch (Exception e) {
            throw new RuntimeException(e);
        }
        return document;
    }

    public static Document stringToXml(String xmlStr) {
        Transformer identityTransformer;
        Source source;
        DOMResult result;
        try {
            source = new StreamSource(new StringReader(xmlStr)); // not a real file, no need to close
            result = new DOMResult();
            TransformerFactory transformerFactory = TransformerFactory.newInstance();
            transformerFactory.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
            identityTransformer = transformerFactory.newTransformer();
            identityTransformer.setOutputProperty(OutputKeys.INDENT, "yes");
            identityTransformer.transform(source, result);
            return (Document) result.getNode();
        } catch (Throwable e) {
        } // failed to transform text to doc?

        return null;
    }

